updates.created:
function(doc, req) {
      if (!doc.created) {
        if (!doc._id) {
          doc.id = req.uuid
        }
        var created = Math.round(Date.now()/1000);
        doc.created = created;
        if (doc.auth_code){
          doc.expires = created + (doc.session_length || 60);
        }
        else if (doc.token)
        {
	  doc.expires = created + (doc.session_length || 3600); 
	}
      }
      return [doc, "OK"];
    }

